class KitComponent:
    def __init__(self, brand, part_no, qty, part_type, position=None, group=None):
        self.brand = (brand or "").strip()
        self.part_no = (part_no or "").strip()
        self.qty = int(qty) if str(qty).isdigit() else 1
        self.part_type = part_type
        self.position = position
        self.group = group  # "A" / "B" for 2KC, None otherwise
        self.clean_pn = re.sub(r"[^a-zA-Z0-9]", "", self.part_no).lower()

def load_font(size):
    for fp in FONT_CANDIDATES:
        if os.path.exists(fp):
            try: return ImageFont.truetype(fp, size)
            except: pass
    return ImageFont.load_default()

def trim_white(img):
    if img.mode != "RGBA":
        img = img.convert("RGBA")
    bg = Image.new("RGBA", img.size, (255, 255, 255, 255))
    diff = ImageChops.difference(img, bg)
    diff = ImageChops.add(diff, diff, 2.0, -100)
    bbox = diff.getbbox()
    if bbox:
        return img.crop(bbox)
    return img

def _norm_brand(s: str) -> str:
    b = re.sub(r"[^a-zA-Z0-9]", "", (s or "").lower())
    # strip common suffixes that appear in file/brand naming
    for suffix in ("dimensions", "dimension", "filters", "filter"):
        if b.endswith(suffix):
            b = b[: -len(suffix)]
            break
    return b

def _to_float(v) -> Optional[float]:
    try:
        return float(str(v).replace(",", ".").strip())
    except Exception:
        return None

def load_dimensions_from_folder(folder_path: str) -> Dict[str, Dict[str, Tuple[float, float, float]]]:
    """
    Load dimensions from CSV files in a folder.
    Each CSV represents a brand; filename (without extension) must match Brand.
    Filters rows where Pk Qty == 1 and Pk UOM == EA.
    Returns: {brand_norm: {part_no_clean: (H, W, L)}}
    """
    dim_map: Dict[str, Dict[str, Tuple[float, float, float]]] = {}
    if not folder_path or not os.path.isdir(folder_path):
        return dim_map

    for p in Path(folder_path).glob("*.csv"):
        brand_key = _norm_brand(p.stem)
        if not brand_key:
            continue
        try:
            with open(p, "r", encoding="utf-8-sig") as f:
                reader = csv.DictReader(f)
                if not reader.fieldnames:
                    continue
                # normalize headers
                headers = {h.strip().lower(): h for h in reader.fieldnames if h}
                col_part = headers.get("part #")
                col_qty = headers.get("pk qty")
                col_uom = headers.get("pk uom")
                col_h = headers.get("height")
                col_w = headers.get("width")
                col_l = headers.get("length")
                if not (col_part and col_qty and col_uom and col_h and col_w and col_l):
                    continue

                for row in reader:
                    part_raw = (row.get(col_part, "") or "").strip()
                    if not part_raw:
                        continue
                    qty = str(row.get(col_qty, "") or "").strip()
                    uom = str(row.get(col_uom, "") or "").strip().upper()
                    if qty != "1" or uom != "EA":
                        continue
                    h = _to_float(row.get(col_h, ""))
                    w = _to_float(row.get(col_w, ""))
                    l = _to_float(row.get(col_l, ""))
                    if h is None or w is None or l is None:
                        continue
                    part_clean = re.sub(r"[^a-zA-Z0-9]", "", part_raw).lower()
                    if not part_clean:
                        continue
                    dim_map.setdefault(brand_key, {})[part_clean] = (h, w, l)
        except Exception:
            continue

    return dim_map

def remove_white_bg(img, thresh=WHITE_THRESH):
    if img.mode != "RGBA":
        img = img.convert("RGBA")
    data = img.getdata()
    new_data = []
    for r, g, b, a in data:
        if r >= thresh and g >= thresh and b >= thresh:
            new_data.append((r, g, b, 0))
        else:
            new_data.append((r, g, b, a))
    img.putdata(new_data)
    return img

def remove_bg_adaptive(img, base_thresh=WHITE_THRESH, tol=18):
    """
    Adaptive background removal based on edge color sampling.
    Removes pixels close to the sampled background color.
    """
    if img.mode != "RGBA":
        img = img.convert("RGBA")
    w, h = img.size
    if w == 0 or h == 0:
        return img

    # Sample edge pixels
    samples = []
    pix = img.load()
    step = max(1, min(w, h) // 50)
    for x in range(0, w, step):
        samples.append(pix[x, 0])
        samples.append(pix[x, h - 1])
    for y in range(0, h, step):
        samples.append(pix[0, y])
        samples.append(pix[w - 1, y])

    # Average background color
    r_avg = sum(p[0] for p in samples) / len(samples)
    g_avg = sum(p[1] for p in samples) / len(samples)
    b_avg = sum(p[2] for p in samples) / len(samples)

    # Adjust threshold based on brightness
    brightness = (r_avg + g_avg + b_avg) / 3.0
    thresh = min(255, max(200, int(brightness + (base_thresh - 240))))

    data = img.getdata()
    new_data = []
    for r, g, b, a in data:
        if abs(r - r_avg) <= tol and abs(g - g_avg) <= tol and abs(b - b_avg) <= tol and r >= thresh and g >= thresh and b >= thresh:
            new_data.append((r, g, b, 0))
        else:
            new_data.append((r, g, b, a))
    img.putdata(new_data)
    return img

def remove_white_bg_cv(img, thresh=240, dilate_px=2, blur_px=1):
    """
    OpenCV-based white background removal. Returns RGBA PIL image.
    """
    if img.mode != "RGBA":
        img = img.convert("RGBA")
    np_img = np.array(img)
    bgr = cv2.cvtColor(np_img, cv2.COLOR_RGBA2BGR)
    gray = cv2.cvtColor(bgr, cv2.COLOR_BGR2GRAY)

    if blur_px > 0:
        k = blur_px * 2 + 1
        gray = cv2.GaussianBlur(gray, (k, k), 0)

    mask = cv2.inRange(gray, thresh, 255)
    if dilate_px > 0:
        kernel = np.ones((dilate_px, dilate_px), np.uint8)
        mask = cv2.dilate(mask, kernel, iterations=1)

    alpha = 255 - mask
    rgba = cv2.cvtColor(bgr, cv2.COLOR_BGR2BGRA)
    rgba[:, :, 3] = alpha
    return Image.fromarray(rgba, "RGBA")

def remove_white_bg_lab(img):
    """
    OpenCV LAB-based white background removal. Returns RGBA PIL image.
    """
    if img.mode != "RGBA":
        img = img.convert("RGBA")
    np_img = np.array(img)
    bgr = cv2.cvtColor(np_img, cv2.COLOR_RGBA2BGR)

    rgb = cv2.cvtColor(bgr, cv2.COLOR_BGR2RGB)
    lab = cv2.cvtColor(rgb, cv2.COLOR_RGB2LAB)

    white_mask = cv2.inRange(
        lab,
        np.array([220, 120, 120], dtype=np.uint8),
        np.array([255, 140, 140], dtype=np.uint8)
    )

    mask = cv2.bitwise_not(white_mask)

    kernel = np.ones((3, 3), np.uint8)
    mask = cv2.morphologyEx(mask, cv2.MORPH_CLOSE, kernel)
    mask = cv2.morphologyEx(mask, cv2.MORPH_OPEN, kernel)
    mask = cv2.erode(mask, kernel, iterations=1)
    mask = cv2.GaussianBlur(mask, (5, 5), 0)

    rgba = cv2.cvtColor(rgb, cv2.COLOR_RGB2RGBA)
    rgba[:, :, 3] = mask
    return Image.fromarray(rgba, "RGBA")

def remove_bg_white_robust(img,
                           hsv_v_min=235, hsv_s_max=35,
                           lab_L_min=215, lab_ab_tol=18,
                           close_k=3, open_k=3,
                           dilate_iter=1,
                           blur_k=3):
    """
    Robust near-white background removal using HSV + LAB.
    Returns RGBA PIL image. Only alpha changes; RGB pixels (labels/text) stay intact.
    """
    global _DEBUG_CLEAN_PRINTED
    if DEBUG_CLEAN and not _DEBUG_CLEAN_PRINTED:
        print("remove_bg_white_robust: active for PartB")
        _DEBUG_CLEAN_PRINTED = True
    if img.mode != "RGBA":
        img = img.convert("RGBA")

    np_img = np.array(img)
    bgr = cv2.cvtColor(np_img, cv2.COLOR_RGBA2BGR)

    # HSV: background if very bright (V high) and low saturation (S low)
    hsv = cv2.cvtColor(bgr, cv2.COLOR_BGR2HSV)
    _h, s, v = cv2.split(hsv)
    bg_hsv = (v >= hsv_v_min) & (s <= hsv_s_max)

    # LAB: background if high L and near-neutral a/b (~128)
    lab = cv2.cvtColor(bgr, cv2.COLOR_BGR2LAB)
    l, a, b = cv2.split(lab)
    bg_lab = (l >= lab_L_min) & (np.abs(a.astype(np.int16) - 128) <= lab_ab_tol) & (np.abs(b.astype(np.int16) - 128) <= lab_ab_tol)

    # Combine background masks
    bg = (bg_hsv | bg_lab).astype(np.uint8) * 255

    # Foreground mask = inverse background
    fg = cv2.bitwise_not(bg)

    # Morphology to remove fringe/holes
    if close_k > 0:
        k = np.ones((close_k, close_k), np.uint8)
        fg = cv2.morphologyEx(fg, cv2.MORPH_CLOSE, k, iterations=1)
    if open_k > 0:
        k = np.ones((open_k, open_k), np.uint8)
        fg = cv2.morphologyEx(fg, cv2.MORPH_OPEN, k, iterations=1)

    # Slightly expand foreground to kill white halo
    if dilate_iter > 0:
        k = np.ones((3, 3), np.uint8)
        fg = cv2.dilate(fg, k, iterations=dilate_iter)

    # Feather edges
    if blur_k > 1 and blur_k % 2 == 1:
        fg = cv2.GaussianBlur(fg, (blur_k, blur_k), 0)

    rgba = cv2.cvtColor(bgr, cv2.COLOR_BGR2BGRA)
    rgba[:, :, 3] = fg
    return Image.fromarray(cv2.cvtColor(rgba, cv2.COLOR_BGRA2RGBA), "RGBA")

def defringe_alpha(rgba: Image.Image, erode=1, blur=3) -> Image.Image:
    """
    Removes white/gray halo by tightening alpha edges slightly + feathering.
    Does NOT repaint labels/text; only alpha is adjusted.
    """
    if rgba.mode != "RGBA":
        rgba = rgba.convert("RGBA")

    arr = np.array(rgba)
    alpha = arr[:, :, 3]
    if erode > 0:
        k = np.ones((3, 3), np.uint8)
        alpha = cv2.erode(alpha, k, iterations=erode)
    if blur and blur > 1 and blur % 2 == 1:
        alpha = cv2.GaussianBlur(alpha, (blur, blur), 0)

    arr[:, :, 3] = alpha
    return Image.fromarray(arr, "RGBA")

def remove_white_spill(rgba: Image.Image, strength=0.30) -> Image.Image:
    """
    Reduces white spill on edge pixels where alpha is small.
    """
    if rgba.mode != "RGBA":
        rgba = rgba.convert("RGBA")

    arr = np.array(rgba).astype(np.float32)
    rgb = arr[:, :, :3]
    a = arr[:, :, 3:4] / 255.0
    edge = (a > 0.0) & (a < 0.95)
    rgb[edge.squeeze()] = np.clip(
        (rgb[edge.squeeze()] - 255.0 * strength * (1.0 - a[edge.squeeze()])) / np.maximum(a[edge.squeeze()], 1e-3),
        0, 255
    )
    arr[:, :, :3] = rgb
    return Image.fromarray(arr.astype(np.uint8), "RGBA")

def keep_largest_component(rgba: Image.Image) -> Image.Image:
    """
    Keep only the largest connected component in alpha mask.
    """
    if rgba.mode != "RGBA":
        rgba = rgba.convert("RGBA")
    arr = np.array(rgba)
    alpha = arr[:, :, 3]
    mask = (alpha > 0).astype(np.uint8)
    if mask.max() == 0:
        return rgba
    num_labels, labels, stats, _ = cv2.connectedComponentsWithStats(mask, connectivity=8)
    if num_labels <= 1:
        return rgba
    # label 0 is background
    largest = 1 + np.argmax(stats[1:, cv2.CC_STAT_AREA])
    new_alpha = np.where(labels == largest, alpha, 0).astype(np.uint8)
    arr[:, :, 3] = new_alpha
    return Image.fromarray(arr, "RGBA")

def remove_bg_ai(img: Image.Image) -> Image.Image:
    """
    AI background removal (rembg) + halo cleanup.
    """
    if img.mode != "RGBA":
        img = img.convert("RGBA")
    cut = remove(img)
    cut = defringe_alpha(cut, erode=1, blur=3)
    cut = remove_white_spill(cut, strength=0.30)
    return cut

def fit_image(img, w, h):
    old_w, old_h = img.size
    ratio = min(w / old_w, h / old_h)
    new_size = (max(1, int(old_w * ratio)), max(1, int(old_h * ratio)))
    return img.resize(new_size, Image.Resampling.LANCZOS)

def make_placeholder(text, w, h):
    img = Image.new("RGB", (w, h), (240, 240, 240))
    d = ImageDraw.Draw(img)
    try:
        f = load_font(20)
        bbox = d.multiline_textbbox((0, 0), text, font=f)
        tx, ty = (w - (bbox[2]-bbox[0]))//2, (h - (bbox[3]-bbox[1]))//2
        d.multiline_text((tx, ty), text, font=f, fill=(0,0,0))
    except: pass
    return img

