class ImageFinder:
    def __init__(self, mode, path, temp_dir=None):
        self.mode = mode
        # "path" can be a single path or a list of paths
        if isinstance(path, (list, tuple)):
            self.paths = [Path(p) for p in path if str(p).strip()]
        else:
            self.paths = [Path(path)] if str(path).strip() else []
        self.path = self.paths[0] if self.paths else Path("")
        self.temp_dir = temp_dir
        self.cloud_map = {}
        self.local_files = []

        if self.mode == "cloud":
            self.load_cloud_csv()
        elif self.mode == "local":
            self.scan_local_files()

    def scan_local_files(self):
        for base in self.paths:
            if not base.exists():
                continue
            for root, _, files in os.walk(base):
                for f in files:
                    if Path(f).suffix.lower() in EXTS:
                        self.local_files.append(Path(root) / f)

    def load_cloud_csv(self):
        """
        Loads one or more Cloud CSV files and builds a (brand, clean_partno) -> url map.
        If the same key appears multiple times, the last occurrence wins.
        """
        try:
            total = 0
            for csv_path in self.paths:
                if not csv_path.exists():
                    continue
                with open(csv_path, "r", encoding="utf-8-sig") as f:
                    reader = csv.DictReader(f)
                    count = 0
                    for row in reader:
                        row_lower = {k.lower(): v for k, v in row.items()}

                        brand = row_lower.get("brand", "").strip().lower()
                        img_raw = row_lower.get("image", "").strip()  # e.g. '1234.jpg'
                        url = row_lower.get("gcp_path", "").strip()

                        if not img_raw:
                            img_raw = row_lower.get("partno", "").strip()

                        if brand and img_raw and url:
                            base_name = os.path.splitext(img_raw)[0]
                            clean_pn = re.sub(r"[^a-zA-Z0-9]", "", base_name).lower()
                            self.cloud_map[(brand, clean_pn)] = url
                            count += 1
                    total += count
            print(f"Ucitano {total} linkova iz Cloud CSV-a.")
        except Exception as e:
            print(f"Cloud CSV Error: {e}")

    def is_valid_filename(self, filename):
        fname = filename.lower()
        for bad in FORBIDDEN_WORDS:
            if bad in fname: return False
        return True

    def calculate_score(self, path_obj, part_no_clean):
        score = 0
        fname = path_obj.stem.lower()
        ext = path_obj.suffix.lower()
        fclean = re.sub(r"[^a-zA-Z0-9]", "", fname)
        parent = str(path_obj.parent).lower()

        if part_no_clean not in fclean:
            return -1

        if "original" in parent:
            score += 1000
        if ext == ".png":
            score += 50

        # Remove only the first occurrence to inspect remainder safely
        idx = fclean.find(part_no_clean)
        if idx == -1:
            return -1
        remainder = fclean[:idx] + fclean[idx + len(part_no_clean):]
        # Reject numeric remainder to avoid 2004 matching 20040/20042
        if remainder and any(ch.isdigit() for ch in remainder):
            return -1
        match_type_score = 0
        found_valid_suffix = False

        if remainder == "":
            match_type_score = 500
            found_valid_suffix = True
        else:
            for idx, suf in enumerate(ALLOWED_SUFFIXES):
                if suf == "": continue
                if suf in remainder:
                    match_type_score = 400 - (idx * 10)
                    found_valid_suffix = True
                    break

        if not found_valid_suffix:
            return -1

        score += match_type_score
        return score

    def find_image(self, component: KitComponent):
        found_path = None
        if self.mode == "local":
            candidates = []
            for p in self.local_files:
                if not self.is_valid_filename(p.name): continue
                score = self.calculate_score(p, component.clean_pn)
                if score > 0:
                    candidates.append((score, p))
            candidates.sort(key=lambda x: x[0], reverse=True)
            if candidates: found_path = candidates[0][1]
        elif self.mode == "cloud":
            key = (component.brand.lower(), component.clean_pn)
            url = self.cloud_map.get(key)
            if url:
                ext = os.path.splitext(url)[1]
                if not ext: ext = ".jpg"
                local_name = f"{component.brand}_{component.clean_pn}{ext}"
                local_file = self.temp_dir / local_name
                if local_file.exists():
                    found_path = local_file
                else:
                    try:
                        headers = {'User-Agent': 'Mozilla/5.0'}
                        r = requests.get(url, headers=headers, timeout=10)
                        if r.status_code == 200:
                            with open(local_file, "wb") as f:
                                f.write(r.content)
                            found_path = local_file
                    except:
                        pass
        return found_path


