def get_layout_grid(n):
    if n <= 3: return 1, [n]
    if n <= 8:
        top = math.ceil(n/2); bot = n - top
        return 2, [top, bot]
    if n == 9: return 3, [3, 3, 3]
    if n == 10: return 3, [4, 3, 3]
    if n == 11: return 3, [4, 4, 3]
    if n == 12: return 3, [4, 4, 4]
    rows = math.ceil(n / 4)
    cols = [4] * (rows-1)
    cols.append(n - sum(cols))
    return rows, cols

def _build_kit_image_flow(kit_name, components, img_finder, output_file, prog_bar_inner=None, layout_name="Oil Set"):
    flat_items = []
    for c in components:
        for _ in range(c.qty):
            flat_items.append(c)

    n = len(flat_items)
    if prog_bar_inner:
        prog_bar_inner['value'] = 0
        prog_bar_inner['maximum'] = n

    canvas = Image.new("RGB", CANVAS_SIZE, BG_COLOR)
    preset = get_layout_preset(layout_name)
    gap = int(preset.get("gap", GAP))
    margin = int(preset.get("margin", MARGIN))
    y_offset = int(preset.get("y_offset", 0))
    cell_scale = float(preset.get("cell_scale", 1.0))

    if n == 0:
        canvas.save(output_file)
        return []

    avail_w = CANVAS_SIZE[0] - (2 * margin)
    max_h = CANVAS_SIZE[1] - (2 * margin)

    missing = []
    rows = []
    cur_row = []
    cur_w = 0

    # Load images + precompute sizes
    items = []
    for item in flat_items:
        img_path = img_finder.find_image(item)
        item_img = None
        if img_path:
            try:
                loaded = Image.open(img_path)
                item_img = remove_bg_adaptive(trim_white(loaded), base_thresh=240, tol=30)
                item_img = remove_white_bg(item_img, thresh=240)
            except Exception:
                pass
        if not item_img:
            item_img = make_placeholder(f"{item.brand}\n{item.part_no}", 300, 300)
            if item.part_no not in missing:
                missing.append(item.part_no)
        items.append((item, item_img))

    # Choose a row count that keeps rows balanced (diff <= 1) and minimizes empty space
    best_rows = None
    best_h = None
    best_score = None
    max_rows = min(n, 5)
    for k in range(1, max_rows + 1):
        base = n // k
        rem = n % k
        counts = [base + (1 if i < rem else 0) for i in range(k)]
        if any(c == 0 for c in counts):
            continue

        # compute max possible height per row so it fits width
        row_max_h = []
        idx = 0
        for c in counts:
            row_ratios = []
            for i in range(c):
                item, img = items[idx + i]
                row_ratios.append(img.width / max(1, img.height))
            idx += c
            denom = sum(row_ratios)
            if denom <= 0:
                row_max_h.append(0)
            else:
                row_max_h.append((avail_w - gap * max(0, c - 1)) / denom)

        min_row_h = min(row_max_h) if row_max_h else 0
        if min_row_h <= 0:
            continue

        # also fit in height
        h_fit = (max_h - gap * max(0, k - 1)) / k
        h = min(min_row_h, h_fit)
        if h <= 0:
            continue

        # score: sum of empty space + vertical empty
        idx = 0
        empty_score = 0
        for c in counts:
            row_ratios = []
            for i in range(c):
                item, img = items[idx + i]
                row_ratios.append(img.width / max(1, img.height))
            idx += c
            row_w = sum(r * h for r in row_ratios) + gap * max(0, c - 1)
            empty = max(0, avail_w - row_w)
            empty_score += empty

        total_h = k * h + gap * max(0, k - 1)
        vert_empty = max(0, max_h - total_h)
        score = empty_score + vert_empty * 2.0
        # Avoid a single long row when there are many items
        if k == 1 and n >= 6:
            score += max_h * max_h

        if best_score is None or score < best_score:
            best_score = score
            best_rows = counts
            best_h = h

    if not best_rows:
        best_rows = [n]

    # Build rows using best distribution
    idx = 0
    for c in best_rows:
        row = []
        for _ in range(c):
            item, img = items[idx]
            idx += 1
            row.append((item, img))
        rows.append(row)

    # Use single height for all rows for consistency
    row_heights = [max(1, int(best_h)) for _ in rows]
    total_h = sum(row_heights) + gap * max(0, len(rows) - 1)
    y_start = margin + max(0, (max_h - total_h) // 2) + y_offset

    # Draw rows
    y = y_start
    for row, rh in zip(rows, row_heights):
        row_w = 0
        dims = []
        for _item, _img in row:
            ratio = _img.width / max(1, _img.height)
            w_i = max(1, int(ratio * rh))
            dims.append(w_i)
            row_w += w_i
        row_w += gap * max(0, len(row) - 1)
        x = margin + max(0, (avail_w - row_w) // 2)
        for (item, img), w in zip(row, dims):
            resized = fit_image(img, w, rh)
            px = x
            py = y
            canvas.paste(resized, (px, py), resized if resized.mode == "RGBA" else None)
            x += w + gap
            if prog_bar_inner:
                prog_bar_inner['value'] += 1
        y += rh + gap

    canvas.save(output_file, quality=95)
    return missing

def _build_kit_image_staggered(kit_name, components, img_finder, output_file, prog_bar_inner=None, layout_name="Oil Set"):
    # Staggered/overlapped rows for Oil Set bottles + filter
    flat_items = []
    for c in components:
        for _ in range(c.qty):
            flat_items.append(c)

    n = len(flat_items)
    if prog_bar_inner:
        prog_bar_inner['value'] = 0
        prog_bar_inner['maximum'] = n

    canvas = Image.new("RGB", CANVAS_SIZE, BG_COLOR)
    preset = get_layout_preset(layout_name)
    margin = int(preset.get("margin", MARGIN))
    y_offset = int(preset.get("y_offset", 0))

    if n == 0:
        canvas.save(output_file)
        return []

    # Load images
    items = []
    missing = []
    for item in flat_items:
        img_path = img_finder.find_image(item)
        item_img = None
        if img_path:
            try:
                loaded = Image.open(img_path)
                item_img = trim_white(loaded)
            except Exception:
                pass
        if not item_img:
            item_img = make_placeholder(f"{item.brand}\n{item.part_no}", 300, 300)
            if item.part_no not in missing:
                missing.append(item.part_no)
        items.append((item, item_img))

    # Determine dominant part (bottles)
    counts = {}
    for item, _img in items:
        counts[item.part_no] = counts.get(item.part_no, 0) + 1
    if not counts:
        canvas.save(output_file)
        return missing
    dominant_part = max(counts.items(), key=lambda kv: kv[1])[0]

    primary = [(it, im) for it, im in items if it.part_no == dominant_part]
    secondary = [(it, im) for it, im in items if it.part_no != dominant_part]

    if not primary:
        canvas.save(output_file)
        return missing

    # Layout parameters
    overlap_x = 0.50
    overlap_y = 0.70
    shift_x = 0.25

    avail_w = CANVAS_SIZE[0] - (2 * margin)
    avail_h = CANVAS_SIZE[1] - (2 * margin)

    # Bottle aspect ratio
    base_img = primary[0][1]
    aspect = base_img.width / max(1, base_img.height)

    n_primary = len(primary)
    best_rows = None
    best_bottle_w = 0

    max_rows = min(n_primary, 5)
    for k in range(1, max_rows + 1):
        base = n_primary // k
        rem = n_primary % k
        counts_per_row = [base + (1 if i < rem else 0) for i in range(k)]
        if any(c == 0 for c in counts_per_row):
            continue

        row_factors = [1 + (c - 1) * (1 - overlap_x) for c in counts_per_row]
        width_factors = [row_factors[i] + (shift_x * i) for i in range(k)]
        max_w_factor = max(width_factors) if width_factors else 1.0
        height_factor = 1 + (k - 1) * (1 - overlap_y)

        bottle_w_limit = avail_w / max_w_factor
        bottle_w_limit_h = (avail_h / height_factor) * aspect
        bottle_w = min(bottle_w_limit, bottle_w_limit_h)
        if bottle_w > best_bottle_w:
            best_bottle_w = bottle_w
            best_rows = counts_per_row

    if not best_rows:
        best_rows = [n_primary]
        best_bottle_w = min(avail_w, avail_h * aspect)

    bottle_w = max(1, int(best_bottle_w))
    bottle_h = max(1, int(bottle_w / max(0.001, aspect)))

    step_x = int(bottle_w * (1 - overlap_x))
    step_y = int(bottle_h * (1 - overlap_y))
    shift_px = int(bottle_w * shift_x)

    row_factors = [1 + (c - 1) * (1 - overlap_x) for c in best_rows]
    width_factors = [row_factors[i] + (shift_x * i) for i in range(len(best_rows))]
    total_w = int(max(width_factors) * bottle_w)
    total_h = int((1 + (len(best_rows) - 1) * (1 - overlap_y)) * bottle_h)

    start_x = margin + max(0, (avail_w - total_w) // 2)
    start_y = margin + max(0, (avail_h - total_h) // 2) + y_offset

    # Place bottles (top row first, bottom row last so bottom is in front)
    idx = 0
    for row_idx, count_in_row in enumerate(best_rows):
        y = start_y + row_idx * step_y
        x0 = start_x + row_idx * shift_px
        for j in range(count_in_row):
            item, img = primary[idx]
            idx += 1
            x = x0 + j * step_x
            resized = fit_image(img, bottle_w, bottle_h)
            canvas.paste(resized, (x, y), resized if resized.mode == "RGBA" else None)
            if prog_bar_inner:
                prog_bar_inner['value'] += 1

    # Place filter (secondary) at left of first row, 75% of bottle size
    if secondary:
        sec_item, sec_img = secondary[0]
        f_h = max(1, int(bottle_h * 0.75))
        f_w = max(1, int(f_h * (sec_img.width / max(1, sec_img.height))))
        fx = start_x
        fy = start_y + (bottle_h - f_h)
        f_resized = fit_image(sec_img, f_w, f_h)
        canvas.paste(f_resized, (fx, fy), f_resized if f_resized.mode == "RGBA" else None)
        if prog_bar_inner:
            prog_bar_inner['value'] += 1

    canvas.save(output_file, quality=95)
    return missing

